import React, {Component, Fragment} from 'react';

// JSX 需要一个最外层的标签包裹住
// 如果不想用标签包裹，可以使用占位符（Fragment）标签
class TodoList extends Component {
    render() {
        return (
            <div className="shopping-list">
                <h1>Shopping List for {this.props.name}</h1>
                <ul>
                    <li>Instagram</li>
                    <li>WhatsApp</li>
                    <li>Oculus</li>
                </ul>
            </div>
        );
    }
}
