import React, {Component} from 'react';

class TodoItem extends Component {
    render() {
        // 接收父组件传过来的值
        return <li onClick={this.handleClick} >{this.props.content}</li>
    }

    handleClick() {
        // 子组件调用父组件方法 来修改父组件的内容
        alert(this.props.index);
    }
}

export default TodoItem;
