import React, {Component, Fragment} from 'react';
import './style.css';
import {CSSTransition} from 'react-transition-group';

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            show: true
        }
        this.handleToggle = this.handleToggle.bind(this);
    }

    render() {
        return (
            <Fragment>
                <CSSTransition
                    in={this.state.show}
                    timeout={1000}
                    classNames='fade'
                    unmountOnExit
                    onEntered={(el) => {
                        el.style.color = 'blue'
                    }}
                    appear={true}
                >
                    {/*appear 第一次显示也要显示效果*/}
                    {/*onEntered 入场动画结束之后执行*/}
                    <div>hello</div>
                </CSSTransition>
                <button onClick={this.handleToggle}>toggle</button>
            </Fragment>
        )
    }

    handleToggle() {
        this.setState({
            show: this.state.show ? false : true
        })
    }
}

export default App;
