import React, {Component} from 'react';
import store from './store';
class TodoList extends Component{
    constructor(props){
        super(props);
        this.state  = store.getState();
    }
    render()
    {
        return (
            <div>
                <div>
                    <input type="text" />
                    <button>提交</button>
                </div>
                <ul>
                    <li>LYJ</li>
                </ul>
            </div>
        )
    }
}
export default TodoList;
