const defaultState = {
    inputValue: 'hello world',
    list: []
}

export default (state = defaultState, action) => {
    if (action === 'change_input_value') {
        // 深拷贝
        const newState = JSON.parse(JSON.stringify(state));
        newState.inputValue = action.inputValue;
        return newState;
    }
    return state;
}
