import React, {Component} from 'react';
import store from './store/index';
import {getInputChangeAction, getAddItemAction, delItemAction,initListAction} from './store/actionCreators';
import TodoListUI from './TodoListUI';
import axios from  'axios';
class TodoList extends Component {

    constructor(props) {
        super(props);
        this.state = store.getState();
        console.log(this.state);
        console.log(store.getState());
        this.handleInputChange = this.handleInputChange.bind(this);
        this.handleStoreChange = this.handleStoreChange.bind(this);
        this.handleBtnClick = this.handleBtnClick.bind(this);
        this.handleItemDelete = this.handleItemDelete.bind(this);
        // store 内容发生改变，subscribe内函数就会自动自行
        store.subscribe(this.handleStoreChange)
    }

    render() {
        return (
            <TodoListUI
                inputValue={this.state.inputValue}
                list={this.state.list}
                handleInputChange = {this.handleInputChange}
                handleBtnClick = {this.handleBtnClick}
                handleItemDelete = {this.handleItemDelete}
            />
        )
    }

    componentDidMount()
    {
        axios.get('http://www.hemaisi.com/service/msg/get?page=1&limit=5').then((res)=>{
            const arr = res.data.data;
            let list = new Array();
            for (let v of arr) {
                list.push(v['ip_city']);
            }
            const action = initListAction(list);
            console.log(action);
            store.dispatch(action);

            // this.setState(() => ({
            //     list: [...list]
            // }))
        });
    }

    handleInputChange(e) {
        const action = getInputChangeAction(e.target.value);
        store.dispatch(action);
        console.log(e.target.value);
    }

    handleItemDelete(index) {
        const action = delItemAction(index);
        store.dispatch(action);
    }

    handleStoreChange() {
        // console.log('store handleStoreChange');
        this.setState(store.getState());
    }

    handleBtnClick() {
        const action = getAddItemAction();
        store.dispatch(action);
    }
}


export default TodoList;
